# initializing dictionary test_dict = {'nikhil' : 1, "akash" : 2, 'akshat' : 3, 'manjeet' : 4} 	# printing original list print("The original dictionary : " + str(test_dict)) # Using dictionary comprehension + items() # Extracting specifix keys from dictionary res = {key: test_dict[key] for key in test_dict.keys() and {'akshat', 'nikhil'}}   # print result print("The filtered dictionary is : " + str(res)) #~ -----------------------------------------------------------------------------# Using dict() # Extracting specifix keys from dictionary res = dict((k, test_dict[k]) for k in ['nikhil', 'akshat'] if k in test_dict)   # print result print("The filtered dictionary is : " + str(res)) #~ -----------------------------------------------------------------------------test_dict = [{'value': 'apple', 'blah': 2},  {'value': 'banana', 'blah': 3} ,  {'value': 'cars', 'blah': 4}]	 # printing original list print("The original dictionary : " + str(test_dict)) 	 	 res = [d['value'] for d in test_dict if 'value' in d]  # print result print("The filtered dictionary is : " + str(res)) #~ -----------------------------------------------------------------------------import mcGetWindow as gw# initializing dictionary test_dict_1 = gw.getWindowsWithTitle('Blender')for w in test_dict_1:	print ("title:",w.title)	# printing original list print("The original dictionary 1: " + str(test_dict_1)) 		 res_1 = [d.title for d in test_dict_1 ]# print result print("The filtered dictionary 1 is :\n\t{}".format( "\n\t".join(res_1))) #~ -----------------------------------------------------------------------------