import Blender#GET OBJECT TO REPLACEcube = Blender.Object.Get('Cube')#CREATE EMPTY MESHobject = Blender.Object.New('Mesh')#NAME THE EMPTY MESHobject.setName('CubeLoc_0')#MOVE THE MESH TO THE LOCATION OF THE CUBEobject.setLocation( cube.LocX, cube.LocY, cube.LocZ )#INDIVIDUALLY ADJUST ROTATION FOR X Y AND Zobject.RotX = cube.RotXobject.RotY = cube.RotYobject.RotZ = cube.RotZ#GET SCENE AND LINK NEWLY MADE OBJECT TO ITscn = Blender.Scene.GetCurrent()scn.link(object)