// Check to see if we need to use Invoke before wasting time with itif (axFlash.InvokeRequired){    // invoke Dispose on the control's native thread to avoid the COM exception    axFlash.Invoke(() => axFlash.Dispose());}else{    // run dispose on this thread    axFlash.Dispose();}//ORpublic void Dispose()  {      activexControl.Dispose();      Marshal.ReleaseComObject(activexControl);}//ORprivate void RunStuff() {     using (DoStuff stuff = new DoStuff())    {        stuff.PerformStuff();     }} 