             string _WebContents = null;            // Read HTML contents from a web page            _WebContents = DownloadHTMLPage(url);            // Show HTML contents            if ( _WebContents != null ) Console.WriteLine(_WebContents);  /// <summary>        /// Function to download HTML web page        /// </summary>        /// <param name="_URL">URL address to download web page</param>        /// <returns>HTML contents as a string</returns>        public string DownloadHTMLPage(string _URL) {            string _PageContent = null;            try {                // Open a connection                HttpWebRequest _HttpWebRequest = ( HttpWebRequest )HttpWebRequest.Create(_URL);                // You can also specify additional header values like the user agent or the referer: (Optional)                _HttpWebRequest.UserAgent = "Mozilla/4.0 (compatible; MSIE 7.0; Windows NT 5.1)";                _HttpWebRequest.Referer = "http://www.google.com/";                // set timeout for 10 seconds (Optional)                _HttpWebRequest.Timeout = 10000;                // Request response:                WebResponse _WebResponse = _HttpWebRequest.GetResponse();                // Open data stream:                Stream _WebStream = _WebResponse.GetResponseStream();                // Create reader object:                StreamReader _StreamReader = new StreamReader(_WebStream);                // Read the entire stream content:                _PageContent = _StreamReader.ReadToEnd();                // Cleanup                _StreamReader.Close();                _WebStream.Close();                _WebResponse.Close();            } catch ( Exception _Exception ) {                // Error                Console.WriteLine("Exception caught in process: {0}", _Exception.ToString());                return null;            }            return _PageContent;        }