            base.OnPaint(e);            Graphics g = e.Graphics;            g.SmoothingMode = SmoothingMode.AntiAlias;            g.InterpolationMode = InterpolationMode.High;            // draw dashed border            Pen p = new Pen(Color.Orange, 2) { DashStyle = DashStyle.Dash };            g.DrawRectangle(p, 0, 0, Width, Height);            // draw grid            if (GridVisible) g.DrawGrid(new Rectangle(0, 0, Width, Height), GridSize, GridColor);            //Size size = new Size(GridSize, GridSize);            //Rectangle rect = new Rectangle(0, 0, Width, Height);            //ControlPaint.DrawGrid(g, rect, size, Color.FromArgb(141, 88, 88, 88));