        private int _RequestTimeout = 10;        private readonly CookieContainer _CookieContainer = new CookieContainer();        private readonly HttpWebResponse webresponse = null;            Uri uri = new Uri(url);            HttpWebRequest req = ( HttpWebRequest )WebRequest.Create(uri.AbsoluteUri);            req.AllowAutoRedirect = true;            req.MaximumAutomaticRedirections = 3;            req.KeepAlive = true;            req.Timeout = _RequestTimeout * 1000;            req.CookieContainer = new CookieContainer();            req.CookieContainer.Add(_CookieContainer.GetCookies(uri));            try {                webresponse = ( HttpWebResponse )req.GetResponse();            } catch ( Exception ex ) {                webresponse = null;                Console.Write("request for url failed: {0} {1}", url, ex.Message);            }            if ( webresponse != null ) {                webresponse.Cookies = req.CookieContainer.GetCookies(req.RequestUri);                // handle cookies (need to do this incase we have any session cookies)                foreach ( System.Net.Cookie retCookie in webresponse.Cookies ) {                    bool cookieFound = false;                    foreach ( System.Net.Cookie oldCookie in _CookieContainer.GetCookies(uri) ) {                        if ( retCookie.Name.Equals(oldCookie.Name) ) {                            oldCookie.Value = retCookie.Value;                            cookieFound = true;                        }                    }                    if ( !cookieFound ) {                        _CookieContainer.Add(retCookie);                    }                }                StreamReader stream = new StreamReader(webresponse.GetResponseStream());                Console.WriteLine(stream.ReadToEnd());                webresponse.Close();            }