    private byte[] ExtractResource(String filename) {            System.Reflection.Assembly a = System.Reflection.Assembly.GetExecutingAssembly();            using (Stream resFilestream = a.GetManifestResourceStream(filename)) {                if (resFilestream == null) return null;                byte[] ba = new byte[resFilestream.Length];                resFilestream.Read(ba, 0, ba.Length);                return ba;            }        }        private Stream GetFlashStream() {            System.Reflection.Assembly a = System.Reflection.Assembly.GetExecutingAssembly();            string fpath = a.GetName().Name + "." + "FLash.dll";            Console.WriteLine("dll fpath:{0}", fpath);            return a.GetManifestResourceStream(fpath);            //return a.GetManifestResourceStream(@"C:\Aprog\Orien\FlashC#\PubliBridge\WalkerPlayer\WalkerPlayer\Flash.dll");        }