        // Process all files in the directory passed in, recurse on any directories        // that are found, and process the files they contain.        public void GetDirectoriesWithFilesRecursive(string targetDirectory, List<string> pathList) {            pathList.Add(targetDirectory);            // Process the list of files found in the directory.            string[] fileEntries = Directory.GetFiles(targetDirectory);            foreach ( string fileName in fileEntries )                 pathList.Add(fileName);            // Recurse into subdirectories of this directory.            string[] subdirectoryEntries = Directory.GetDirectories(targetDirectory);            foreach ( string subdirectory in subdirectoryEntries )                GetDirectoriesWithFilesRecursive(subdirectory, pathList);        }                    string appSystemDir = Path.Combine(BookUpdaterForm.Ns_Apps_Dir, "system.app");                    GetDirectoriesWithFilesRecursive(appSystemDir, files);                    string[] entries = Directory.GetFileSystemEntries(appSystemDir, "*", SearchOption.AllDirectories);