    //events                    this.SizeChanged += new System.EventHandler(this.OnMainFormSizeChanged);            this.Resize += new System.EventHandler(this.OnMainFormResize);//Handle Resize Form         private void OnMainFormResize(object sender, System.EventArgs e) {            Control form = (Control)sender;            Size new_size = mcMath.FitSizeProportionally(FlashPlayer.Size, form.Size, Flash_Margins);            FlashPlayer.Width = new_size.Width;            FlashPlayer.Height = new_size.Height;        }        private void OnMainFormSizeChanged(object sender, System.EventArgs e) {           Control form = (Control)sender;            Size new_size = mcMath.FitSizeProportionally(FlashPlayer.Size, form.Size, Flash_Margins);            FlashPlayer.Width = new_size.Width;            FlashPlayer.Height = new_size.Height;        }