        public static void OrienTools() {            AppDomain.CurrentDomain.AssemblyResolve += new ResolveEventHandler(AssemblyResolver);        }        static Assembly AssemblyResolver(object sender, ResolveEventArgs args) {            Assembly thisAssembly = Assembly.GetExecutingAssembly();            //this is the resource name of the assembly I want to load            string resourceName = "Orien.NetUi.Orien.Tools.dll";            Stream resourceStream = thisAssembly.GetManifestResourceStream(resourceName);            //convert the stream to something we can load as an assembly            byte[] buffer = new byte[resourceStream.Length];            resourceStream.Read(buffer, 0, buffer.Length);            Assembly referencedAssembly = Assembly.Load(buffer);            return referencedAssembly;        }