https://www.arclab.com/en/kb/csharp/save-and-restore-position-size-windows-forms-application.htmlprivate void Form1_Closing(object sender, FormClosingEventArgs e){    Properties.Settings.Default.F1State = this.WindowState;    if (this.WindowState==FormWindowState.Normal)    {        // save location and size if the state is normal        Properties.Settings.Default.F1Location = this.Location;        Properties.Settings.Default.F1Size = this.Size;    }    else    {        // save the RestoreBounds if the form is minimized or maximized!        Properties.Settings.Default.F1Location = this.RestoreBounds.Location;        Properties.Settings.Default.F1Size = this.RestoreBounds.Size;    }    // don't forget to save the settings    Properties.Settings.Default.Save();}