        private int suspendCounter = 0;        private const int WM_SETREDRAW = 11;        [DllImport("user32.dll")]        public static extern int SendMessage(IntPtr hWnd, Int32 wMsg, bool wParam, Int32 lParam);        private void SuspendDrawing()        {            if (suspendCounter == 0)                SendMessage(this.Handle, WM_SETREDRAW, false, 0);            suspendCounter++;        }        private void ResumeDrawing()        {            suspendCounter--;            if (suspendCounter == 0)            {                SendMessage(this.Handle, WM_SETREDRAW, true, 0);                this.Refresh();            }        }