                string arg = "";            //switch (typeof(args_str)) {            switch ((args_str).GetType().ToString()) {                case "int":                    arg = "";                    break;                case "string":                    arg = "";                    break;                case "double":                    arg = "";                    break;                case "object":                    arg = "";                    break;                default:                    arg = "";                    break;            };            mcGlobal.console.AppendText("arg:" + arg);        private static void TestTypeSwitch() {            var ts = new TypeSwitch()                .Case((int x) => Console.WriteLine("int"))                .Case((bool x) => Console.WriteLine("bool"))                .Case((string x) => Console.WriteLine("string"))                .Case((object x) => Console.WriteLine("object"));            ts.Switch(42);            ts.Switch(false);            ts.Switch("hello");            ts.Switch(new Point(10,20));        }    public class TypeSwitch {        Dictionary<Type, Action<object>> matches = new Dictionary<Type, Action<object>>();        public TypeSwitch Case<T>(Action<T> action) { matches.Add(typeof(T), (x) => action((T)x)); return this; }        public void Switch(object x) { matches[x.GetType()](x); }    }