unit Utils;interfaceuses System.SysUtils;type  TKeyValue<T> = class  private    FKey: string;    FValue: T;    procedure SetKey(const Value: string);    procedure SetValue(const Value: T);  public    property Key: string read FKey write SetKey;    property Value: T read FValue write SetValue;  end;implementation// Usage// kv: TKeyValue<TButton>;// kv := TKeyValue<TButton>.Create;// kv.Key := 'mykey';procedure TKeyValue<T>.SetKey(const Value: string);begin  FKey := Value;end;procedure TKeyValue<T>.SetValue(const Value: T);begin  FValue := Value;end;end.